# interface utilisateur
def interface_utilisateur
end

# question 1 : combien y a t'il de handle dans cette array ?
def how_many_handle (journalist)
    how_many_handle = journalist.length
    puts "nous avons #{how_many_handle} handle parmis cette liste d'emails qui me fait fumer le cerveau."
end

# question 2 : quel est le handle le plus court de cette liste ?
def shortest_handle (journalist)
    sort_shortest = journalist.min_by {|x| x.length}
    puts "voici le foutu handle le plus court : #{sort_shortest}"
end

# question 3 : Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
def five_handle (journalist)
    five_handle = journalist.count {|x| x.length == 6 }
    puts "voici le nombre de handle contenant 5 caractères : #{five_handle}"
end
        

# question 4 : Combien commencent par une majuscule (première lettre juste après le @) ?
def maj_handle (journalist)
    maj_handle = journalist.count {|handle| handle[1].match?( /[[:upper:]]/ )} 
    puts "voici le nombre de handle qui commencent par une majuscule : #{maj_handle} "
end

# question 5 : Trier la liste de handle par ordre alphabétique.
def order_handle (journalist)
    order_handle = journalist.sort { |a, b| a <=> b }
    puts "voici cette foutue liste dans l'ordre alphabétique : #{order_handle}"
end

# question 6 : Trier la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
def size_handle (journalist)
    size_handle = journalist.sort_by {|x| x.length}
    puts "voici la liste par taille de handle : #{size_handle}"
end

# question 7 : Quelle est la position dans l'array de la personne @epenser ?
def position_handle (journalist)
    position_handle = journalist.find_index ("@epenser")
    puts "voici la position de @epenser dans l'array : #{position_handle}"
end

# question 8 : Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
def repartition_handle (journalist)
    repartition_handle = journalist.join('').size
    puts "voici la répartition des handle : #{repartition_handle}"
end




